INSERT INTO `locations` (`id`, `name`, `slug`, `description`, `map`, `thumbnail`, `media`, `hotel_count`, `combo_count`, `landtour_count`, `voucher_count`, `homestay_count`, `review_count`, `is_featured`, `created_at`, `updated_at`) VALUES
	(4, 'HÀ NỘI', 'ha-noi', 'Hà Nội là thủ đô ngàn năm văn hiến, còn lưu dấu nhiều di tích Hồ Gươm, Cầu Thê Húc, Chùa Quán Sứ, Hồ Tây, 36 phố phường. Hà Nội có bốn mùa, luôn mang đến nhiều hoài niệm khó phai, mỗi mùa một vẻ, xuân lễ hội, hạ tươi thắm, thu quyến rũ và đông ấn tượng. Món ngon có Phở, Chả cá Lã Vọng, bánh tôm Hồ Tây.\r\n\r\n', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d59587.97785448771!2d105.80194413492788!3d21.02273601629448!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ab9bd9861ca1%3A0xe7887f7b72ca17a9!2zSGFub2ksIEhvw6BuIEtp4bq_bSwgSGFub2k!5e0!3m2!1sen!2s!4v1543652942480" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\Hinh-anh-nen-Ha-Noi.jpg', '', 379, 0, 0, 0, 2, 10, 1, '2018-12-01 15:29:44', '2019-01-21 09:46:48'),
	(5, 'HỒ CHÍ MINH', 'ho-chi-minh', 'Được mệnh danh là “Hòn ngọc Viễn Đông”, TP.HCM từ lâu đã là trung tâm văn hóa, kinh tế, chính trị của Việt Nam.\r\n\r\nDu lịch đến với Sài Gòn – TP.HCM hơn 300 năm tuổi, bạn có thể gặp những tòa nhà cao tầng nằm san sát, những khu vui chơi giải trí, trung tâm mua sắm sầm uất, nhưng cũng không thiếu những biệt thự cổ kính, những ngôi chợ truyền thống tồn tại đã hàng trăm năm. Sài Gòn rộng lớn và không thiếu những “đặc sản” du lịch như Du ngoạn ven sông Sài Gòn bằng tàu, thăm phố Tây Phạm Ngũ Lão, mua sắm ở chợ Bến Thành hay về với biển Cần Giờ…', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d501725.3382259616!2d106.41502848792288!3d10.755341089952083!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x317529292e8d3dd1%3A0xf15f5aad773c112b!2sHo+Chi+Minh+City%2C+Vietnam!5e0!3m2!1sen!2s!4v1543653036038" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\hinh-anh-thanh-pho-ho-chi-minh-cuc-dep_031031379.png', '', 208, 0, 0, 0, 0, 10, 1, '2018-12-01 15:31:05', '2019-01-21 09:45:11'),
	(6, 'ĐÀ NẴNG', 'da-nang', 'Du lịch Đà Nẵng điểm đến lý tưởng, thành phố Đà Nẵng xanh, sạch đẹp, sở hữu bãi biển xinh đẹp bờ cát thoai thoải, trắng mịn được tạp chí Forbes bình chọn là 1 trong 6 bãi biển quyến rũ nhất hành tinh. TP. Đà Nẵng với dòng sông Hàn thơ mộng cùng những cây cầu đã làm nên biểu tượng của phố biển. Đà Nẵng còn sở hữu nhiều danh thắng tuyệt đẹp làm xiêu lòng du khách như khu du lịch Bà Nà Hills, Bán Đảo Sơn Trà, Ngũ Hành Sơn… Ngoài ra Đà Nẵng là trung tâm của Miền Trung, có 1 vị trí đắc địa nằm trên con đường di sản Miền Trung từ Đà Nẵng bạn có thể đến với 3 di sản văn hóa thế giới là Cố đô Huế, phố cổ Hội An và thánh địa Mỹ Sơn.', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d61349.62126476666!2d108.17168640746648!3d16.047248394807504!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x314219c792252a13%3A0xfc14e3a044436487!2zRGEgTmFuZywgSOG6o2kgQ2jDonUsIERhIE5hbmcsIFZpZXRuYW0!5e0!3m2!1sen!2s!4v1543653227700" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\5057_SYc_mau_Ya_NYng_-_Mai_Thanh_ChYYng_1.jpg', '', 253, 0, 0, 3, 0, 10, 1, '2018-12-01 15:34:06', '2019-01-21 09:44:07'),
	(7, 'HUẾ', 'hue', 'Huế như cô gái đằm thắm, kiêu kỳ, khiến ai cũng đều đắm say khi ở lại và ngẩn ngơ thương nhớ mỗi khi ra về.\r\nT\r\nrong đôi mắt của người yêu cái đẹp, Huế hiện lên thật tình với chiếc nón bài thơ, bóng dáng thấp thoáng thướt tha những tà áo dài, sông Hương hiền hòa một chiều gió lộng, những câu hát gợi nhớ thương.\r\n\r\nMảnh đất cố đô yên bình, mộng mơ là điểm dừng chân của những tâm hồn lạc lối với trăm nỗi bộn bề cuộc sống, là cuộc gặp gỡ của những người đang đi tìm cảm hứng sáng tác nghệ thuật, thi ca. Hay đơn giản, Huế là nơi lữ khách dừng chân, thưởng ngoạn thành phố miền Trung yên ả, rồi trót yêu, trót gắn bó chẳng nỡ rời đi.', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d61222.96854771765!2d107.54209355859076!3d16.453469860520382!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3141a115e1a7935f%3A0xbf3b50af70b5c7b7!2sHu%E1%BA%BF%2C+Thua+Thien+Hue%2C+Vietnam!5e0!3m2!1sen!2s!4v1543653257419" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\check-in-hue-1.jpg', '', 61, 0, 0, 0, 0, 7, 0, '2018-12-01 15:34:52', '2019-01-21 09:42:27'),
	(8, 'PHÚ QUỐC', 'phu-quoc', 'Đảo Phú Quốc thuộc tỉnh Kiên Giang, được mệnh danh là đảo ngọc, là hòn đảo lớn nhất Việt Nam nằm trong vịnh Thái Lan. Đảo Phú Quốc nổi tiếng với những bãi biển trong xanh, những dòng suối yên bình và hải sản tươi ngon.\r\n\r\n', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d251289.7061112148!2d103.81733648128296!3d10.22915336566681!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a78c62b49eda17%3A0x8aa79fbbdd72cdb!2zUGjDuiBRdeG7kWM!5e0!3m2!1sen!2s!4v1543653913036" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\1470302452_anh-5.jpg', '', 210, 0, 1, 3, 0, 15, 1, '2018-12-01 15:45:53', '2019-01-21 09:41:30'),
	(9, 'SAPA', 'sapa', 'Thị trấn Sapa thuộc tỉnh Lào Cai, có địa hình 1600 mét cao hơn mực nước biển. Khí trời mát mẻ vùng núi và cảnh quan đẹp hoang sơ chính là những yếu tố giúp Sapa trở thành một khu nghỉ mát nổi tiếng ở miền Bắc Việt Nam. Tại đây, du khách có thể chinh phục đỉnh Phan Si Păng của dãy Hoàng Liên Sơn, ngắm vườn hoa Hàm Rồng và khám phá thung lũng Mường Hoa. Các địa điểm tham quan khác khi du lịch Sapa bao gồm: Thác Bạc, Thác Tình Yêu, Bản Hồ, Chợ phiên Sapa…', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d59042.55124655058!2d103.78191874647456!3d22.34760733639915!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x36cd416833ee9ad5%3A0xe1f42da2bbc76727!2stt.+Sa+Pa%2C+Sa+Pa%2C+Lao+Cai%2C+Vietnam!5e0!3m2!1sen!2s!4v1543653991746" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\sapa.jpg', '', 158, 0, 0, 2, 0, 15, 1, '2018-12-01 15:49:12', '2019-01-21 09:40:40'),
	(10, 'NHA TRANG', 'nha-trang', 'Vốn nổi tiếng là thành phố biển du lịch của Việt Nam, Nha Trang có rất nhiều những danh lam thắng cảnh hút hồn các du khách bởi vẻ đẹp tuyệt vời. Khu giải trí Vinpearl Land với nhiều trò chơi thú vị và hấp dẫn, đảo Hòn Mun với những rặng san hô đẹp lộng lẫy, còn Hòn Tằm thu hút du khách với bãi tắm tuyệt đẹp hay dịch vụ tắm bùn khoáng tại I-Resort, khu du lịch Trăm Trứng sẽ giúp du khách thư giãn sau ngày dài. Đến Nha Trang không quên thưởng thức đặc sản nổi tiếng phố biển như bún sứa, nem nướng\r\n\r\n', '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d249525.44778403552!2d109.10641382999812!3d12.25943461453622!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3170677811cc886f%3A0x5c4bbc0aa81edcb9!2sNha+Trang%2C+Khanh+Hoa+Province%2C+Vietnam!5e0!3m2!1sen!2s!4v1543654201628" width="600" height="450" frameborder="0" style="border:0" allowfullscreen></iframe>', 'locations\\NhaTrang-KhoaTran-27-1616120145.jpg', '', 185, 0, 0, 1, 0, 14, 1, '2018-12-01 15:50:56', '2019-01-21 10:16:40'),
	(11, 'VŨNG TÀU', 'vung-tau', 'Cách trung tâm thành phố Hồ Chí Minh chỉ khoảng 3 tiếng lái xe, với đường bờ biển trải dài 20km, Vũng Tàu là một trong những điểm đến yêu thích của du khách phía Nam. Nằm nhô hẳn ra khỏi đất liền như một dải đất, từ nơi đây, người ta có thể nhìn biển Đông', '', 'locations\\Tổng-quan-du-lịch-Vũng-Tàu-ivivu.jpg', '', 68, 0, 0, 2, 0, 10, 0, '2019-01-03 17:46:52', '2019-01-03 17:46:52'),
	(12, 'QUẢNG BÌNH', 'quang-binh', 'Hiếm có nơi nào ở Việt Nam lại tập trung nhiều danh lam thắng cảnh đẹp và địa hình đa dạng như Quảng Bình. Không chỉ nổi tiếng bởi các hang động kỳ vĩ ở vườn quốc gia Phong Nha Kẻ Bàng. Quảng Bình còn có các bãi biển hoang sơ như: biển Nhật Lệ, biển Đá Nh', '', 'locations\\4.jpg', '', 36, 0, 0, 0, 0, 10, 0, '2019-01-03 17:48:41', '2019-08-03 14:08:08'),
	(13, 'PHÚ YÊN', 'phu-yen', 'Là một tỉnh thuộc duyên hai nam trung bộ hướng bắc giáp Bình Định, hướng nam giáp Khánh Hòa, hướng tây giáp Đăk lăk vs Gia lai hướng đông giáp biển đông, ở giữa hai đèo đó là đèo Cả và đèo Cù Mông. là tỉnh có địa hình đồng bằng đồi núi thung lũng xen kẽ n', '', 'locations\\dia-diem-du-lich-phu-yen-5-1.jpg', '', 24, 0, 0, 0, 0, 4, 0, '2019-01-03 17:49:45', '2019-01-03 17:49:45'),
	(14, 'NINH BÌNH', 'ninh-binh', 'Với thiên nhiên hoang sơ, phong cảnh non nước hữu tình, Ninh Bình hiện đang là một trong những điểm đến nổi tiếng của miền Bắc. Đến với Ninh Bình, du khách sẽ có dịp tham quan những di sản thiên nhiên và di sản văn hóa thế giới Tràng An, chùa Bái Đính..', '', 'locations\\anh-ninh-binh-1.jpg', '', 55, 0, 0, 0, 0, 10, 0, '2019-01-03 17:53:25', '2019-01-03 17:53:25'),
	(15, 'ĐÀ LẠT', 'da-lat', 'Đà Lạt là thủ phủ của tỉnh Lâm Đồng. Với độ cao 1.500 m trên mặt nước biển, Đà Lạt tiết trời mát lạnh và là nơi nghỉ dưỡng lý tưởng ở khu vực miền Nam. Từng một thời nổi tiếng với các điểm tham quan như Thung lũng Tình Yêu, Hồ Than Thở, Đồi Thông Hai Mộ..', '', 'locations\\DN-Anh-chup-Da-Lat-BaiDN-221217-1.jpg', '', 134, 0, 0, 0, 1, 20, 1, '2019-01-03 17:57:30', '2019-01-03 17:57:30'),
	(16, 'PHAN THIẾT', 'phan-thiet', 'PHAN THIẾT là một địa điểm du lịch nổi tiếng của Phan Thiết được rất nhiều người biết đến với những bờ cát trắng trải dài, biển xanh và những hàng dừa cong vút. Đến Mũi Né, bạn không chỉ có tham quan, ngắm cảnh mà còn có thể thưởng thức vô vàn những món ăn...', '', 'locations\\bai-rang-mui-ne.jpg', '', 80, 0, 0, 0, 0, 10, 0, '2019-01-03 17:58:25', '2019-11-12 11:00:05'),
	(17, 'QUY NHƠN', 'quy-nhon', 'Thuộc tỉnh Bình Định, thành phố Quy Nhơn khá yên bình với sự kết hợp hài hòa giữa núi và biển. Cũng có cát trắng, nắng vàng, biển xanh, thế nhưng cái tên Quy Nhơn cũng gợi lên chút gì đó “nắng nóng, oi ả, hoang sơ” khiến nhiều người mới nghe thôi cũng thấy ngần ngại. Tuy nhiên, nếu chỉ vì vậy mà bỏ qua Quy Nhơn thì chắc chắn bạn đang bỏ qua một vùng đất rất nhiều điều thú vị. Bởi cảnh sắc chưa bị khai thác du lịch nhiều và cuộc sống bình dị, đầy quyến rũ nơi đây hoàn toàn đủ để chinh phục bất kì ai trót một lần dừng chân. Và khi có cơ hội ghé thăm mảnh đất vùng biển này, hãy chắc rằng bạn sẽ dành đủ thời gian để tận hưởng trọn vẹn cảnh sắc, cuộc sống nơi đây chân thực nhất nhé. Cẩn thận đấy, Quy Nhơn nhỏ thế thôi mà “nội công thì thâm hậu vô cùng”.', '', 'locations\\top-10-dia-diem-du-lich-co-view-dep-va-hap-dan-tha-ho-check-in-tai-quy-nhon-202106151431049339.jpg', '', 80, 0, 0, 1, 0, 10, 0, '2019-01-03 18:16:25', '2019-01-03 18:16:25'),
	(18, 'HÒA BÌNH', 'hoa-binh', 'Cách Hà Nội không xa, Hòa Bình là một trong những địa điểm du lịch được rất nhiều bạn trẻ ưa thích bởi cảnh sắc núi rừng thiên nhiên hoang sơ đầy thơ mộng.\r\nThiên nhiên ban tặng cho Hòa Bình rất nhiều phong cảnh hùng vĩ tráng lệ, những thung lũng quanh năm mây mờ che phủ, những ngọn thác bình lặng chảy ngày chảy đêm, những hang động kì bí và những nét văn hóa vô cùng đặc sắc của các dân tộc miền núi đang sinh sống tại đây', '', 'locations\\ttxvnthuydienhoabinh.jpg', '', 32, 0, 0, 0, 0, 10, 0, '2019-01-03 18:25:11', '2019-01-03 18:25:11'),
	(19, 'CẦN THƠ', 'can-tho', 'Cần Thơ gạo trắng nước trong. Ai đi đến đó thì không muốn về" Câu ca dao lưu truyền từ bao đời đã làm lay động lòng người mỗi khi có dịp dừng chân ghé thăm vùng đất Tây Đô. Bến Ninh Kiều, chợ nổi Cái Răng, vườn cỏ Bằng Lăng hay vườn trái cây trĩu quả chắc hẳn sẽ níu chân du khách khi đặt chân đến Cần Thơ.Cuộc sống nơi đây mang đậm nét của một vùng quê sông nước.Với lợi thế này, Cần Thơ đang là một điểm du lịch hấp dẫn của miền Tây.\r\n\r\n', '', 'locations\\can-tho-bridge-vietnam-1a.jpg', '', 32, 0, 0, 0, 0, 10, 0, '2019-01-21 09:38:12', '2019-01-21 09:38:12'),
	(21, 'BUÔN MA THUỘT', 'buon-ma-thuot', 'Ban Mê Thuột (Buôn Mê Thuột) luôn hấp dẫn, mùa khô rừng cao su đổ lá, mùa mưa với tiếng gầm thét của dòng Serepok cuộn trào, tháng 3 “mùa con ong đi lấy mật” và là mùa lễ đua voi, mùa lúa chín rộn ràng. Cụm thác Dray Sap, Dray Nur, Gia Long của Ban Mê Thuột ẩn chứa huyền thoại giàu có về văn hóa, phong tục của M’Nông, Ê đê.\r\n\r\n', '', 'locations\\mach-nuoc-10-diem-chup-anh-o-buon-ma-thuot-dep-chat-ngat-01.jpg', '', 12, 0, 0, 0, 0, 10, 0, '2019-01-21 10:18:53', '2019-01-21 10:18:53'),
	(22, 'MỘC CHÂU', 'moc-chau', 'Mộc Châu mang trong mình những đồi chè xanh ngút ngàn, những cao nguyên tràn đầy nhựa sống, dải lụa trắng được dệt từ ngàn bông cải,chén rượu ngô ấm nồng men say,… tất cả đã thu hút bất cứ ai một lần đặt chân tới nơi này.', '', 'locations\\camnhi-181009011020-tong-quan-ve-moc-chau-son-la.jpg', '', 22, 0, 0, 0, 0, 10, 0, '2019-01-21 10:24:36', '2019-01-21 10:24:36'),
	(23, 'CÀ MAU', 'ca-mau', 'Cà Mau quê hương của Bác Ba Phi, là thành phố trẻ 300 năm, là cực nam Việt Nam với 3 mặt tiếp giáp biển. Điểm hấp dẫn của Cà Mau là các đình quán cổ, khu di tích lịch sử, và tham quan vườn chim trong Rừng U Minh. Đặc sản nổi tiếng của Cà Mau phải kể đến mắm cá lóc, ba khía, tôm cua sò…\r\n\r\n', '', 'locations\\mui-ca-mau-1.jpg', '', 6, 0, 0, 0, 0, 10, 0, '2019-01-21 10:58:59', '2019-01-21 10:58:59'),
	(24, 'HỘI AN', 'hoi-an', 'Hội An nằm ở hạ lưu sông Thu Bồn, tỉnh Quảng Nam, là thành phố du lịch nổi tiếng với tên thường gọi “Phố cổ Hội An”. Đến với Hội An là đắm mình vào không gian văn hóa truyền thống với các làng nghề xưa như làng mộc Kim Bồng, làng gốm Thanh Hà, làng đúc đồng Phước Kiều… Khu phố cổ Hội An phô trương phong cách kiến trúc truyền thống và các di tích như Hội Quán Phúc Kiến, Chùa Cầu, miếu Quan Công hay Chùa Ông và nhà thờ tộc Trần. Các món ăn cao lầu, mì Quảng, bánh xèo và lễ hội đua ghe cũng là những yếu tố thu hút nhiều người đến với du lịch Hội An', '', 'locations\\hoi-an-quang-nam-vntrip.jpg', '', 112, 0, 0, 0, 0, 7, 1, '2019-01-21 15:21:00', '2019-01-21 15:21:00'),
	(25, 'THANH HÓA', 'thanh-hoa', 'Đến với vùng đất xứ Thanh “địa linh nhân kiệt” là đến địa danh: Hàm Rồng – Sông Mã. Ai đã từng vào Nam ra Bắc, qua niềm “đất lửa” năm xưa, nơi sơn thủy hữu tình, khí thiêng hội tụ, phát tích nền văn hóa Đông Sơn hẳn còn lưu giữa hình ảnh cầu Hàm Rồng nối đôi bờ Sông Mã, gối đầu lên núi Ngọc, núi Rồng; dòng sông xanh lặng lờ uốn khúc chở nặng phù sa, mang theo điệu hò neo đậu lòng người ngược xuôi của những chàng trai cô gái xứ Thanh… Đến Thanh Hóa du khách còn được thỏa sức tắm biển tại những bãi biển xanh biếc trong xanh đầy quyến rũ.', '', 'locations\\diem-du-lich-thanh-hoa-8_1623941124.jpg', '', 52, 0, 0, 1, 0, 0, 0, '2019-01-22 17:48:49', '2019-01-22 17:48:49'),
	(26, 'HẠ LONG', 'ha-long', 'Hạ Long được bao quanh bởi thành phố Hạ Long, thành phố Cẩm Phả và huyện đảo Vân Đồn thuộc tỉnh Quảng Ninh. Hạ Long đã được UNESCO công nhận là Di sản thiên nhiên thế giới. Đến với Hạ Long, du khách được đi thuyền trên vịnh để ngắm hàng loạt các hòn đảo đá vôi và phiến thạch có cấu trúc và hình dáng hùng vĩ. Một số thắng cảnh nổi bật nên chiêm ngưỡng khi du lịch Hạ Long là Hòn Con Cóc, Hòn Gà Chọi, Đảo Ti Tốp, Đảo Ngọc Vừng, Hang Sửng Sốt, Động Thiên Cung, Hang Đầu Gỗ và vịnh Bái Tử Long. Hạ Long cũng được bầu chọn là một trong 7 kì quan thiên nhiên mới của thế giới.', '', 'locations\\dao-titop-quang-ninh-02_1625285135_1660475000.jpg', '', 218, 0, 0, 0, 1, 10, 1, '2019-01-23 10:04:56', '2019-01-23 10:04:56'),
	(27, 'VĨNH PHÚC ', 'vinh-phuc', 'Nằm cách không xa Hà Nội, Vĩnh Phúc là nơi có nhiều cảnh quan độc đáo, kỳ thú, cùng các di tích lịch sử, văn hóa mang đậm dấu ấn tâm linh. Đây cũng là điểm đến thích hợp cho những kỳ nghỉ ngắn ngày cùng nhóm bạn, gia đình.', '', 'locations\\kham-pha-10-diem-du-lich-noi-tieng-o-vinh-phuc-2.jpg', '', 9, 0, 0, 1, 0, 10, 0, '2019-01-23 10:50:12', '2019-01-23 10:50:12'),
	(28, 'BÌNH DƯƠNG', 'binh-duong', 'Bình Dương là một tỉnh thuộc khu vực Đông Nam Bộ, cách 30 km từ trung tâm thành phố Hồ Chí Minh và nằm trong top 7 tỉnh có dân số đông nhất Việt Nam. Nơi đây được biết đến là “xứ sở của những cơn mưa”. Vào những tháng đầu mùa mưa thường có mưa rào rất lớn, đặc biệt, từ tháng 7 tới tháng 9 là khoảng thời gian xuất hiện những cơn mưa dầm kéo dài 1-2 ngày mới chịu dứt. Tuy nhiên bù lại, Bình Dương được thiên nhiên ưu ái ban cho thảm thực vật vô cùng phong phú và những tài nguyên khoáng sản có giá trị.\r\n\r\n', '', 'locations\\VnE-BinhDuong-KDLThuyChau1-JPG-4095-1605099937.jpg', '', 6, 0, 0, 0, 0, 0, 0, '2019-05-03 15:56:38', '2019-07-25 13:07:13'),
	(29, 'HẢI PHÒNG', 'hai-phong', 'Hải Phòng còn được gọi là Thành phố Hoa phượng đỏ, là thành phố cảng lớn nhất phía Bắc. Là trung tâm du lịch lớn, Hải phòng có những bãi biển lượn quanh, những khu nghỉ mát và giải trí nổi tiếng trong và ngoài nước đạt tiêu chuẩn quốc tế.\r\n\r\n', '', 'locations\\khu-du-lich-bien-do-son-hai-phong.jpg', '', 68, 0, 0, 0, 0, 0, 0, '2019-05-03 15:57:42', '2019-05-03 15:57:42'),
	(30, 'ĐIỆN BIÊN', 'dien-bien', 'Điện Biên là tỉnh miền núi biên giới thuộc vùng Tây Bắc, cách thủ đô Hà Nội gần 500km; phía Bắc giáp tỉnh Lai Châu, phía Đông và Đông bắc giáp tỉnh Sơn La, phía Tây Bắc giáp tỉnh Vân Nam – Trung Quốc; phía Tây và Tây Nam giáp với nước bạn Lào. Đến với Điện Biên là đến với thiên nhiên, là trở về thăm lại một miền lịch sử, hào hùng với những chiến công vang dội.\r\n', '', 'locations\\diểm-du-lịch-diện-biên-phủ-dèo-pha-din-1024x576.jpg', '', 7, 0, 0, 0, 0, 0, 0, '2019-05-03 15:58:56', '2019-05-03 15:58:56'),
	(31, 'HÀ TĨNH', 'ha-tinh', 'Nằm trên mảnh đất đầy nắng gió của Bắc Trung Bộ và cách thủ đô Hà Nội 340 km về phía Nam, tỉnh Hà Tĩnh có lợi thế phía Đông giáp biển, phía Tây giáp dãy Trường Sơn huyền thoại cùng nước Lào. Nhờ vào đặc điểm đó, địa hình ở Hà Tĩnh trở nên đa dạng hơn bao giờ hết từ vùng đồi núi, trung du – đồng bằng đến sông suối, hồ… Đáng chú ý đó chính là đường bờ biển trải dài 137 km, Hà Tĩnh luôn làm du khách phải ngỡ ngàng trước vẻ đẹp của các bãi tắm, trong đó phải kể đến biển Thiên Cầm.\r\n\r\nKhông những sở hữu phong cảnh non nước hữu tình, Hà Tĩnh còn được biết đến là vùng đất hội tụ địa linh nhân kiệt và là cái nôi của văn hóa dân gian, sản sinh ra các làn điệu dân ca, ví giặm.', '', 'locations\\108_10_dia_danh_khong_the_bo_qua_o_ha_tinh.jpg', '', 13, 0, 0, 0, 0, 0, 0, '2019-07-16 12:46:09', '2019-07-16 12:46:09'),
	(32, 'Nghệ An', 'nghe-an', 'Nghệ An là tỉnh có diện tích lớn nhất Việt Nam thuộc vùng Bắc Trung Bộ. Trung tâm hành chính của tỉnh là thành phố Vinh, nằm cách thủ đô Hà Nội 291 km về phía nam', '', 'locations\\Phuong_tinh_ho2_3.jpg', '', 33, 0, 0, 0, 0, 0, 0, '2019-07-25 11:51:04', '2019-07-25 11:51:04'),
	(33, 'Tam Đảo', 'tam-dao', 'Tam Đảo', '', 'locations\\cau-may-tam-dao.jpg', '', 24, 0, 0, 0, 0, 0, 0, '2019-07-28 19:39:21', '2019-07-28 19:39:21'),
	(34, 'QUẢNG TRỊ', 'quang-tri', 'Quảng Trị', '', 'locations\\thanh-co-quang-tri.jpg', '', 6, 0, 0, 0, 0, 0, 0, '2019-07-29 12:23:04', '2019-08-03 14:07:55'),
	(36, 'QUẢNG NgÃI', 'quang-ngai', 'Lý Sơn', '', 'locations\\ly-son-1-e1509706803776.jpg', '', 6, 0, 0, 0, 0, 0, 0, '2019-08-03 14:04:13', '2020-05-09 11:42:32'),
	(37, 'CÔN ĐẢO', 'con-dao', 'Côn Đảo', '', 'locations\\cd9-1648805106-2272-1648805130.jpg', '', 34, 0, 0, 0, 0, 0, 0, '2019-08-05 13:44:46', '2019-08-05 13:44:46'),
	(38, 'PHÚ THỌ ', 'phu-tho', 'Phú Thọ', '', 'locations\\PhuTho-1652263732-4577-1652263744.jpg', '', 7, 0, 0, 0, 0, 0, 0, '2019-08-20 22:20:18', '2019-08-28 23:20:54'),
	(40, 'YÊN BÁI', 'yen-bai', 'Yên Bái', '', 'locations\\cac-dia-diem-du-lich-o-yen-bai.jpg', '', 6, 0, 0, 0, 0, 0, 0, '2019-10-26 10:37:14', '2019-10-26 10:37:14'),
	(41, 'GIA LAI', 'gia-lai', 'Vẻ đẹp của vùng đất Gia Lai, du khách sẽ được ngắm con đường hàng thông, rừng cao su mùa thay lá và vùng Biển Hồ trên cao nguyên.', '', 'locations\\cac-dia-diem-du-lich-o-gia-lai.jpg', '', 9, 0, 0, 0, 0, 0, 0, '2019-10-26 10:38:53', '2019-10-26 10:38:53'),
	(42, 'NINH THUẬN', 'ninh-thuan', 'NINH THUẬN', '', 'locations\\9cb9b8971d8001adH1.jpg', '', 11, 0, 0, 0, 0, 0, 0, '2019-10-29 15:29:49', '2019-10-29 15:29:49'),
	(43, 'BẮC KẠN', 'bac-kan', 'Bắc Kạn có hồ Ba Bể', '', 'locations\\1169_9_dia_danh_nhat_dinh_phai_kham_pha_khi_den_bac_kan.jpg', '', 1, 0, 0, 0, 0, 0, 0, '2020-02-20 14:29:33', '2020-02-20 14:30:52'),
	(44, 'CAO BẰNG', 'cao-bang', 'Cao Bằng có Thác Bản Dốc', '', 'locations\\thac-Ban-Gioc-Cao-Bang-8614-1648729935.jpg', '', 5, 0, 0, 0, 0, 0, 0, '2020-02-20 16:56:54', '2020-02-20 16:56:54'),
	(45, 'LAI CHÂU', 'lai-chau', 'Lai Châu là một tỉnh thuộc vùng Tây Bắc Bộ, Việt Nam', '', 'locations\\10-diem-du-lich-checkin-noi-tieng-nhat-tai-tinh-lai-chau-202303281553481660.jpg', '', 2, 0, 0, 0, 0, 0, 0, '2020-04-10 16:13:30', '2020-04-10 16:13:51'),
	(46, 'LẠNG SƠN', 'lang-son', 'Lạng Sơn là thành phố tỉnh lỵ của tỉnh Lạng Sơn, Việt Nam.', '', 'locations\\545_cam_nang_du_lich_lang_son.jpg', '', 2, 0, 0, 0, 0, 0, 0, '2020-04-16 17:27:43', '2020-04-16 17:27:43'),
	(47, 'TÂY NINH', 'tay-ninh', 'Tây Ninh là thành phố tỉnh lỵ của tỉnh Tây Ninh, Việt Nam. Thành phố Tây Ninh cách trung tâm thành phố Hồ Chí Minh 100 km về phía Tây Bắc, có vị trí chiến lược quan trọng, là nơi giao nhau giữa các quốc lộ 22B, đường đi đến cửa khẩu Mộc Bài và Xa Mát.', '', 'locations\\dia-diem-du-lich-tay-ninh-2_1626672636.jpg', '', 6, 0, 0, 0, 0, 0, 0, '2020-04-16 17:28:39', '2020-04-16 17:28:39'),
	(48, 'HÀ NAM', 'ha-nam', 'Hà Nam là một tỉnh thuộc vùng Đồng bằng sông Hồng, Việt Nam. Năm 2018, Hà Nam là đơn vị hành chính Việt Nam đông thứ 51 về số dân, xếp thứ 44 về Tổng sản phẩm trên địa bàn, xếp thứ 23 về GRDP bình quân đầu người, đứng thứ sáu về tốc độ tăng trưởng GRDP.', '', 'locations\\hnamvietravel-1652345773-16523-1685-7071-1652345811.jpg', '', 4, 0, 0, 0, 0, 0, 0, '2020-04-16 17:29:25', '2020-04-16 17:29:25'),
	(49, 'HÀ GIANG', 'ha-giang', 'Mùa xuân Hà Giang là sắc đào sắc mận nở trắng trời, mùa thu là những thửa ruộng bậc thang vàng óng ả, và khi mùa đông đến cả Hà Giang lại chìm trong sắc hồng dịu ngọt của những cánh đồng tam giác mạch mênh mang nơi miền biên viễn. Nhưng đã khi nào bạn đặt chân tới Hà Giang vào mùa hè chưa nhỉ? Hà Giang mùa hè như chơi với trong sắc xanh bao phủ cả núi rừng, mộc mạc nên thơ nhưng bừng lên niềm hy vọng nơi cao nguyên đá xa xôi.', '', 'locations\\MaPiLengHaGiangVnExpress-16488-3513-7729-1648806038.jpg', '', 13, 0, 0, 0, 0, 0, 0, '2020-08-19 15:50:32', '2020-08-19 15:50:32'),
	(50, 'BẮC GIANG', 'bac-giang', 'Bắc Giang là một tỉnh thuộc vùng Đông Bắc nước ta, nơi được thiên nhiên ưu đãi với nhiều thắng cảnh đẹp lại không cách quá xa Hà Nội, giao thông rất thuận lợi.', '', 'locations\\dia-diem-du-lich-bac-giang-8.jpg', '', 7, 0, 0, 0, 0, 0, 0, '2022-05-19 10:33:32', '2022-05-19 10:33:32'),
	(51, 'ĐỒNG NAI', 'dong-nai', 'Đồng Nai có nhiều di tích lịch sử, văn hoá và các điểm du lịch có tiềm năng: Văn miếu Trấn Biên (Biên Hòa), đền thờ Nguyễn Hữu Cảnh, khu du lịch Bửu Long, khu du lịch ven sông Đồng Nai, Vườn quốc gia Nam Cát Tiên, làng bưởi Tân Triều, khu du lịch sinh thái Thác Mai - hồ nước nóng, Đảo Ó, chiến khu Đ, mộ cổ Hàng Gòn, đàn đá Bình Đa, khu du lịch thác Giang Điền, khu du lịch Long Châu Viên (Xuân Tân, Long Khánh), khu du lịch Vườn Xoài, khu di tích cấp quốc gia núi Chứa Chan (núi Gia Lào), Hồ Núi Le (Xuân Lộc), trung tâm hành hương Đức Mẹ núi Cúi (Gia Kiệm), khu du lịch Suối Mơ, làng du lịch Tre Việt, khu du lịch Bò Cạp Vàng, khu du lịch sinh thái Thuỷ Châu.', '', 'locations\\dia-diem-du-lich-dong-nai-6.jpg', '', 1, 0, 0, 0, 0, 0, 0, '2022-06-13 17:18:40', '2022-06-13 17:18:40'),
	(52, 'LAI CHÂU', 'lai-chau-1', 'Lai Châu có nhiều cao nguyên cao trên 1.500m, mây, sương phủ bốn mùa, khí hậu trong lành, mát lạnh quanh năm như: cao nguyên Sìn Hồ, hồ Thầu, Dào San...\r\nLai Châu có nhiều đỉnh núi cao, sông suối nhiều thác ghềnh, như: dãy Pu Sam Cáp (cao trên 1.700 m), sông Đà, sông Nậm Na, sông Nậm Mu …\r\nSuối nước nóng, nước khoáng là sản vật thiên nhiên tặng cho Lai Châu như núi đá Ô, động Tiên (Sìn Hồ); động Tiên Sơn (Bình Lư - Tam Đường), thác Tắc Tình (TT Tam Đường), suối nước nóng Vàng Bó (Phong Thổ); suối nước nóng Nà Đông, Nà Đon (Tam Đường); suối nước khoáng (Than Uyên);… và các hồ thuỷ điện lớn khác.', '', 'locations\\dia-diem-du-lich-lai-chau-mytour-1.jpg', '', 1, 0, 0, 0, 0, 0, 0, '2022-06-13 17:19:54', '2022-06-13 17:19:54'),
	(53, 'AN GIANG', 'an-giang', 'An Giang là tỉnh có dân số đông nhất và diện tích lớn thứ tư ở miền Tây Nam Bộ Việt Nam. Chính vì rộng như vậy nên An Giang chất chứa trong lòng nhiều cảnh quan du lịch độc đáo thu hút lòng người.\r\nNgoài Rừng Tràm Trà Sư, An Giang còn có Chùa Linh Sơn Ba Thê, Đồi Tức Dụp, Làng Dệt Thổ Cẩm Châu Giang, Làng Người Chăm Châu Giang, Di Chỉ Óc Eo, Núi Ba Thê, Tịnh Biên.', '', 'locations\\cong-troi-an-giang-1.jpg', '', 3, 0, 0, 0, 0, 0, 0, '2022-06-13 17:28:42', '2022-06-13 17:28:42'),
	(54, 'BÌNH THUẬN', 'binh-thuan', 'Là một tỉnh ven biển, khí hậu quanh năm nắng ấm, nhiều bãi biển sạch đẹp, cảnh quan tự nhiên và thơ mộng, giao thông thuận lợi, Bình Thuận đang là một trong những trung tâm du lịch lớn của Việt Nam.', '', 'locations\\21-min-16.jpg', '', 8, 0, 0, 0, 0, 0, 0, '2022-06-14 08:36:09', '2022-06-14 08:36:09'),
	(55, 'BẾN TRE', 'ben-tre', 'Bến Tre có điều kiện thuận tiện để phát triển du lịch sinh thái, bởi ở đó còn giữ được nét nguyên sơ của miệt vườn, giữ được môi trường sinh thái trong lành trong màu xanh của những vườn dừa, vườn cây trái rộng lớn.', '', 'locations\\Con-Quy.jpg', '', 5, 0, 0, 0, 0, 0, 0, '2022-06-14 08:38:22', '2022-06-14 08:38:22'),
	(56, 'TRÀ VINH', 'tra-vinh', 'Tuy là vùng đất trẻ nhưng Trà Vinh có kho tàng văn hóa đa dạng, đặc biệt là văn hóa vật thể và phi vật thể của người Khmer. Người Khmer có chữ viết riêng, các lễ hội truyền thống như Chol chnam thmay (mừng năm mới), Dolta (lễ cúng ông bà), Ok Om Bok (lễ cúng trăng), Dâng bông, Dâng phước và các phong tục tập quán có giá trị văn hóa khác của người Kinh, người Hoa như Lễ hội nghinh Ông tại Mỹ Long (lễ hội nghinh ông diễn ra vào ngày 10 đến 12 tháng 5 hằng năm), Vu lan thắng hội, Tiết Trùng Cửu,...', '', 'locations\\chua-hang-tra-vinh.jpg', '', 1, 0, 0, 0, 0, 0, 0, '2022-06-16 16:26:33', '2022-06-16 16:26:33'),
	(57, 'BẮC NINH', 'bac-ninh', 'Gắn liền với những câu ca quan họ, Bắc Ninh là tọa độ vi vu nổi bật trong các điểm du lịch phía Bắc. Đặc biệt, nơi đây còn được thiên nhiên ưu ái với vô vàn cảnh sắc thiên nhiên tuyệt đẹp cùng nhiều công trình kiến trúc cổ xưa. Có lẽ vậy mà các địa điểm du lịch Bắc Ninh luôn rất thu hút du khách ghé thăm, đặc biệt là những ai mê khám phá văn hóa - lịch sử.', '', 'locations\\image1.png', '', 4, 0, 0, 0, 0, 0, 0, '2022-07-18 15:09:16', '2022-07-18 15:09:16'),
	(58, 'TUYÊN QUANG', 'tuyen-quang', 'Tuyên Quang - Thành phố bên dòng Lô lịch sử, nơi có 59 điểm du lịch lịch sử, văn hoá và duy trì tổ chức nhiều lễ hội văn hoá độc đáo hàng năm như: Lễ hội đua thuyền, lễ hội chùa Hương Nghiêm, lễ hội đền Hạ - đền Thượng - đền Ỷ La, lễ rước cánh ấn đền Trần và lễ hội đường phố. Trong đó có nhiều điểm di tích đền, chùa đã trở thành thế mạnh trong việc phát triển du lịch văn hóa tâm linh của thành phố, hàng năm thu hút một lượng lớn khách du lịch. Nơi đây còn lưu dấu ấn bàn tay tài hoa của con người trên những di tích, kiến trúc nghệ thuật, đình, chùa, đền, miếu... trong đó có nhiều di tích được xếp hạng cấp Quốc gia như: Thành nhà Mạc, đền Hạ, đền Thượng, đền Ỷ La…', '', 'locations\\771_nhung_thang_canh_noi_tieng_tuyen_quang.jpg', '', 3, 0, 0, 0, 0, 0, 0, '2022-08-11 10:38:51', '2022-08-11 10:38:51'),
	(59, 'THÁI BÌNH', 'thai-binh', 'Thái Bình là một tỉnh ven biển ở Đồng bằng sông Hồng, tới Thái Bình bạn sẽ được khám phá những điểm du lịch hấp dẫn từ chùa chiền đến các bãi biển tuyệt đẹp.', '', 'locations\\du-lich-thai-binh-ivivu-1.jpg', '', 0, 0, 0, 0, 0, 0, 0, '2022-08-11 10:51:17', '2022-08-11 10:51:17'),
	(60, 'NAM ĐỊNH', 'nam-dinh', 'Nam Định cách Hà Nội khoảng 100 km. Cung đường từ Hà Nội về Nam Định đẹp, thuận lợi và hiện đại, nên du khách có thể đi bằng tàu hỏa, ô tô hay xe gắn máy. Nam định là một vùng đất giàu bản sắc văn hóa với nhiều lễ hội truyền thống. Nam Định hiện có gần 4.000 di tích lịch sử văn hóa. Nhiều di tích lịch sử văn hóa, kiến trúc tiêu biểu như: Phủ Dầy, chùa Cổ Lễ, chùa Keo Hành Thiện, cầu Ngói, chùa Lương, cột cờ Nam Định. Ngoài ra, còn có các di tích lịch sử văn hóa gắn với các danh nhân như: Trạng Nguyên Nguyễn Hiền, Trạng lường Lương Thế Vinh, nhà thơ Tú Xương, nhà lưu niệm Nguyễn Bính, khu lưu niệm cố Tổng Bí thư Trường Chinh…Cùng với các di tích lịch sử văn hóa, Nam Định còn có trên 70 làng nghề với các ngành nghề và sản phẩm truyền thống như nghề chạm khảm gỗ, sơn mài, đúc đồng, mây tre đan, trồng cây cảnh. Nam Định còn bảo tồn được những di sản văn hóa, tiêu biểu gắn với hơn 100 lễ hội truyền thống được tổ chức hàng năm, nhiều lễ hội với nét độc đáo riêng, có sức hút du khách gần xa về dự hội như: Lễ hội Phủ Dầy, lễ hội Đền Trần, lễ khai ấn đầu năm, hội chợ Viềng…', '', 'locations\\152109052-3554053231314692-4218511051505747866-o.jpg', '', 4, 0, 0, 0, 0, 0, 0, '2022-09-19 14:35:43', '2022-09-19 14:35:43');
